package com.spe.maybe;import java.util.function.Function;// Just a marker interface, Java type system does not allow to abstract Monads into an interface// More details here : https://stackoverflow.com/questions/35951818/why-can-the-monad-interface-not-be-declared-in-javapublic interface Monad<T> {    // monads have 3 fundamental operations :    // return : T1 -> M<T1>    // unbox : M<T> -> T    // bind : M<T> -> (T -> M<U>) -> M<U>    <U> Monad<U> bind(Function<? super T, Monad<U>> mapping);}